<?php

include "config/config.php";

// Verifica se o usuário está logado
if (!isset($_SESSION['uname'])) {
    header('Location: index.php');
    exit;
}

// Logout
if (isset($_POST['but_logout'])) {
    session_destroy();
    header('Location: index.php');
    exit;
}

require 'config/conexao.php';

// Recebe o id do cliente via GET
$id_cliente = isset($_GET['id_cliente']) ? $_GET['id_cliente'] : '';


// Verifica se existe um id válido
if (!empty($id_cliente) && is_numeric($id_cliente)) {

    // Consulta os dados do cliente
    $conexao = conexao::getInstance();
    $sql_membros = 'SELECT id, matricula, nome FROM tab_membros WHERE id = :id';
    $stm_membros = $conexao->prepare($sql_membros);
    $stm_membros->bindValue(':id', $id_cliente);
    $stm_membros->execute();
    $cliente = $stm_membros->fetch(PDO::FETCH_OBJ);
    
    // Agora definimos matricula com o valor correto do banco de dados
    $matricula = $cliente->matricula;

    // Consulta os calibres distintos na tabela tab_armas
    $sql_calibres = 'SELECT DISTINCT calibre FROM tab_habitu_op';
    $stm_calibres = $conexao->prepare($sql_calibres);
    $stm_calibres->execute();
    $calibres = $stm_calibres->fetchAll(PDO::FETCH_OBJ);
    
       // Consulta os grupos distintos na tabela tab_armas
    $sql_grupos = 'SELECT * FROM tab_grupos_armas';
    $stm_grupos = $conexao->prepare($sql_grupos);
    $stm_grupos->execute();
    $grupos = $stm_grupos->fetchAll(PDO::FETCH_OBJ);
}

// Processa o formulário quando enviado
if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    	$user = (isset($_POST['user'])) ? $_POST['user'] : '';
    
    $tipo = $_POST['tipo'];
    $modelo = $_POST['modelo'];    
    $calibre = $_POST['calibre'];
    $numsigma = $_POST['numsigma'];    
    $grupo=$_POST['grupo'];
    $validade_gt = $_POST['validade_gt'];
    $validade_craf = $_POST['validade_craf'];
    $sequencia = $_POST['sequencia'];    
    
      $conexao = conexao::getInstance();
        
        $sql_user = "SELECT * FROM users WHERE username = :name";
        $stm = $conexao->prepare($sql_user);
        
        $stm->bindParam(':name', $user, PDO::PARAM_STR);
        
        $stm->execute();
        
        $usuario = $stm->fetch(PDO::FETCH_OBJ);
        //insere log
    $sql_log = 'INSERT INTO tab_logs (username, tabela, tipo_de_alteracao, registro, created_at)
			   VALUES(:username, :tabela, :tipo_alteracao, :registro, :data)';

			$stm = $conexao->prepare($sql_log);
			$stm->bindValue(':username', $usuario->name);
			$stm->bindValue(':tabela', 'tab_armas');
			$stm->bindValue(':tipo_alteracao', 'criacao');
		  // Converte o array para JSON
            $registro = json_encode(['Arma' => $tipo, 'modelo'=>$modelo, 'calibre'=>$calibre, 'numsigma'=>$numsigma]);
            $stm->bindValue(':registro', $registro);
			$stm->bindValue(':data', date('Y-m-d H:i:s'));
            $retorno_log = $stm->execute();
            
            $imagemBase64 = isset($_POST["imagem"])?$_POST['imagem']:'';
      $imagemCraf=isset($_POST["imagem2"])?$_POST['imagem2']:'';
      
      
     // Decodifica a imagem Base64
    $imagem = str_replace('data:image/jpeg;base64,', '', $imagemBase64);
    $imagemC = str_replace('data:image/jpeg;base64,', '', $imagemCraf);
    $imagem2=base64_decode($imagemC);
    $imagem1 = base64_decode($imagem);
    
   

    // Define o nome e caminho do arquivo
    $nomeArquivo = "uploads/".uniqid() . ".jpg";
$nomeArquivo2 = "uploads/".uniqid() . ".jpg";
    // Salva a imagem original
    file_put_contents($nomeArquivo, $imagem1);
 file_put_contents($nomeArquivo2, $imagem2);
    // Reduz o tamanho da imagem
    $imagemReduzida = imagecreatefromjpeg($nomeArquivo);
        $imagemReduzida2 = imagecreatefromjpeg($nomeArquivo2);
    $novoLargura = 400; 
    $novoAltura = 400;
    $imagemFinal = imagescale($imagemReduzida, $novoLargura, $novoAltura);
    $imagemFinal2 = imagescale($imagemReduzida2, $novoLargura, $novoAltura);
    // Salva a imagem reduzida
    imagejpeg($imagemFinal, $nomeArquivo, 80);
       imagejpeg($imagemFinal2, $nomeArquivo2, 80);
    // Libera memória
    imagedestroy($imagemReduzida);
     imagedestroy($imagemReduzida2);
    imagedestroy($imagemFinal);
 imagedestroy($imagemFinal2);
  

    // Garante que $matricula esteja definido
    if (!empty($matricula)) {
        $sql = 'INSERT INTO tab_armas (id_membro, id_proprietario, matricula, numsigma, id_grupo, tipo, modelo, calibre, validade_gt, validade_craf,imagem_gt, imagem_craf, base64_gt, base64_craf, sequencia) 
                VALUES (:id_membro,:id_membro, :matricula, :numsigma,:grupo, :tipo, :modelo, :calibre, :validade_gt, :validade_craf,:imagem_gt, :imagem_craf,:base64_gt, :base64_craf, :sequencia)';
        $stm = $conexao->prepare($sql);
        $stm->bindValue(':id_membro', $id_cliente);
        $stm->bindValue(':matricula', $matricula);
        $stm->bindValue(':numsigma', $numsigma);
        $stm->bindValue(':grupo', $grupo);
        $stm->bindValue(':tipo', $tipo);
        $stm->bindValue(':modelo', $modelo);
        $stm->bindValue(':calibre', $calibre);
        $stm->bindValue(':validade_gt', $validade_gt);
        $stm->bindValue(':validade_craf', $validade_craf);
          $stm->bindValue(':imagem_gt', $nomeArquivo);
          $stm->bindValue(':imagem_craf', $nomeArquivo2);
          $stm->bindValue(':base64_gt', $imagem);
         $stm->bindValue(':base64_craf', $imagemC);
        $stm->bindValue(':sequencia', $sequencia);    
        $stm->execute();

        header('Location: armas.php?id=' . $id_cliente);
        exit;
    } else {
        echo "Matrícula não definida. Verifique os dados.";
    }
}

