 <script>
        $(document).ready(function () {
            let video = $("#camera")[0];
            let canvas = $("#canvas")[0];
            let preview = $("#preview");
            let imagemInput = $("#imagem");
            let stream = null;
            let useFrontCamera = false;

            function startCamera() {
                let constraints = {
                    video: { facingMode: useFrontCamera ? "user" : "environment" }
                };

                navigator.mediaDevices.getUserMedia(constraints)
                    .then(s => {
                        if (stream) {
                            stream.getTracks().forEach(track => track.stop());
                        }
                        stream = s;
                        video.srcObject = stream;
                        $("#camera, #capture, #uploadForm button, #switchCamera").show();
                        $("#startCamera").hide();
                    })
                    .catch(err => console.error("Erro ao acessar a câmera:", err));
            }

            // Ativa a câmera
            $("#startCamera").click(startCamera);

        

            // Captura a imagem
            $("#capture").click(function () {
                let context = canvas.getContext("2d");
                canvas.width = video.videoWidth;
                canvas.height = video.videoHeight;
                context.drawImage(video, 0, 0, canvas.width, canvas.height);
 $("#camera, #capture, #uploadForm button, #switchCamera").hide();
                let imagemBase64 = canvas.toDataURL("image/jpeg");
                preview.attr("src", imagemBase64).show();
                imagemInput.val(imagemBase64);
            });

            // Envia a imagem via AJAX
            $("#uploadForm").submit(function (e) {
                e.preventDefault();

         
            });
        });
    </script>
