<?php		//**************************************************	// função para formatar CPF e CNPJ | Tiago Moselli	//**************************************************	function formataCPFCNPJ($value){		$cnpj_cpf = preg_replace("/\D/", '', $value);		if(strlen($cnpj_cpf) === 11) {			return preg_replace("/(\d{3})(\d{3})(\d{3})(\d{2})/", "\$1.\$2.\$3-\$4", $cnpj_cpf);		} 		return preg_replace("/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/", "\$1.\$2.\$3/\$4-\$5", $cnpj_cpf);	}	$meses = array(		'01' => 'Janeiro',		'02' => 'Fevereiro',		'03' => 'Março',		'04' => 'Abril',		'05' => 'Maio',		'06' => 'Junho',		'07' => 'Julho',		'08' => 'Agosto',		'09' => 'Setembro',		'10' => 'Outubro',		'11' => 'Novembro',		'12' => 'Dezembro'	);		//**************************************************	include_once("../config/config.php");	include_once ("../config/cabecalho.php");	include_once ("../config/assinatura.php");		// Recebe o id do cliente do cliente via GET	$id_cliente = (isset($_GET['id'])) ? $_GET['id'] : '';	// Valida se existe um id e se ele é numérico	if (!empty($id_cliente) && is_numeric($id_cliente)):		// Captura os dados do cliente solicitado		$conexao = conexao::getInstance();		$sql = 'SELECT * FROM tab_membros WHERE id = :id';		$stm = $conexao->prepare($sql);		$stm->bindValue(':id', $id_cliente);		$stm->execute();		$cliente = $stm->fetch(PDO::FETCH_OBJ);		if(!empty($cliente)):			// Formata a data no formato nacional			$array_data     = explode('-', $cliente->data_nascimento);			$data_formatada = $array_data[2] . '/' . $array_data[1] . '/' . $array_data[0];		endif;	endif;	       $html = '<table border=1>';		   $html .= '<tr><td>'.$cliente->matricula.'</td></tr>';	$html .= '</tbody>';	$html .= '</table>';	//referenciar o DomPDF com namespace	use Dompdf\Dompdf;	// include autoloader	require_once("dompdf/autoload.inc.php");	//Criando a Instancia	$dompdf = new DOMPDF(array('enable_remote' => true));	// Carrega seu HTML	$dompdf->load_html(utf8_encode('<!--*****************************************************************************************************                                DECLARAÇÃO DE SEGURANÇA DE GUARDA DO ACERVO*****************************************************************************************************--><p align=justify style="line-height: 150%; margin-left: 10; margin-right: 10"><br><br><br><Br><h3 style="text-align: center;">DECLARAÇÃO DE SEGURANÇA DO ACERVO</h3><br><br><p align=justify style="line-height: 150%; margin-left: 10; margin-right: 10">EU, <B>'.utf8_decode($cliente->nome).'</b>, nacionalidade <B>'.utf8_decode($cliente->nacionalidade).'</b>, natural de <B>'.utf8_decode($cliente->naturalidade).'</b>, nascido em <b>'.date('d/m/Y', strtotime($cliente->data_nascimento)).'</b>, profissão <B>'.utf8_decode($cliente->profissao).'</b>, estado civil <B>'.utf8_decode($cliente->estadocivil).'</b>, residindo em <B>'.utf8_decode($cliente->rua).'</b> nº <B>'.utf8_decode($cliente->numero).'</b>, bairro <B>'.utf8_decode($cliente->bairro).'</b>, cep: <B>'.utf8_decode($cliente->cep).'</b>, <B>'.utf8_decode($cliente->cidade).'</b>/<B>'.utf8_decode($cliente->siglauf).'</b> e CPF nº <B>'.formataCPFCNPJ($cliente->cpf).'</b>. DECLARO, para fim de <b>CONCESSÃO, REVALIDAÇÃO DE REGISTRO NO COMANDO DO EXÉRCITO OU APOSTILAMENTO</b>, que o local de guarda do meu acervo de <b>ATIRADOR DESPORTIVO</b> possui cofre ou lugar seguro, com tranca, para armazenamento das armas de fogo desmuniciadas de que sou proprietário, e de que adotarei as medidas necessárias para impedir que menor de dezoito anos de idade ou pessoa civilmente incapaz se apodere de arma de fogo sob minha posse ou de minha propriedade, observado o disposto no art. 13 da Lei nº 10.826, de 2003.<br><br><br><br><table width="100%" style="border-collapse: collapse; margin-top: 5px;">        <tr>            <td align="center" style="padding: 5px;"><font size="2"><br><br><br>_________________________________________<br>ATIRADOR(A)<br><b>'.strtoupper(utf8_decode($cliente->nome)).'</b><br>CPF: '.formataCPFCNPJ($cliente->cpf).'</td>            <td align="right" style="padding: 5px;"><font size="2">'.PDF_ASSINA.'</td>        </tr>    </table>					'));	//Renderizar o html	$dompdf->render();	$pagesCount = $dompdf->get_canvas()->get_page_number();		// -------------------------------------------------------	// grava log	// -------------------------------------------------------	$sql = 'INSERT INTO tab_declaracoes 		(matricula, relatorio, data_emissao, ip_emissao) VALUES		(:matricula, :relatorio, :data_emissao, :ip_emissao)	';	$stm = $conexao->prepare($sql);	$stm->bindValue(':matricula', $cliente->matricula);	$stm->bindValue(':relatorio', utf8_encode('Declaração Habitualidade'));	$stm->bindValue(':data_emissao', date('Y-m-d H:i:s'));	$stm->bindValue(':ip_emissao', $_SERVER['REMOTE_ADDR']);	$stm->execute();	// -------------------------------------------------------	$documento = base64_encode($dompdf->output()); 	/* cria documento assinado */	require_once($_SERVER['DOCUMENT_ROOT'].'/autentique/autentique.class.php');    $aut = new AutentiqueH();    $aut->tipo_documento = utf8_encode('Habitualidade').' '.$cliente->matricula.' '.date('d/m/Y H:i:s');    $aut->posicao_assinatura = array(70, 85, $pagesCount);	$aut->criar_documento($documento);	$aut->output();			//Exibibir a pÃ¡gina	$dompdf->stream(		"declaracao_habitualidade", 		array(			"Attachment" => true //Para realizar o download somente alterar para true		)	);		?>