oc($resultado_membros)){    #        #        	$html .= '<tr><td>'.$row_membros['matricula'] . "</td>";	#}		$html .= '</tbody>';	$html .= '</table>';		//referenciar o DomPDF com namespace	use Dompdf\Dompdf;	// include autoloader	require_once("dompdf/autoload.inc.php");	//Criando a Instancia	$dompdf = new DOMPDF(array('enable_remote' => true));		// Carrega seu HTML	$dompdf->load_html(utf8_encode('<!--*****************************************************************************************************                                            CARTEIRA DO CLUBE*****************************************************************************************************--><p align=center style="line-height: 120%; margin-left: 15; margin-right: 15">'.PDF_CABECALHO.'<h3 style="text-align: center;">CARTEIRA DE MEMBRO DO CLUBE</h3><br><div style="width: 500px; margin: auto; font-size: 11px; height: 372px; background: url(https://www.hostmarq.com.br/img/cracha.png);"><br><table align="right" style="padding-top:82px;" width="180">	<tr>		<td align="center" width="50%">'.$cliente->identidade.'</td>		<td align="center" width="50%">'.date('d/m/Y', strtotime($cliente->data_nascimento)).'</td>	</tr></table><table align="right" style="padding-top:105px;" width="180">	<tr>		<td align="center" width="50%">'.formataCPFCNPJ($cliente->cpf).'</td>		<td align="center" width="50%">ABC+</td>	</tr></table><table align="right" style="padding-top:128px;" width="180">	<tr>		<td align="center" width="50%">xxxxxxxx</td>		<td align="center" width="50%">'.$cliente->cr.'</td>	</tr></table><table align="left" style="padding-top:265px; font-size: 16px" width="180">	<tr>		<td align="center">'.$cliente->matricula.'</td>	</tr></table><table align="left" style="padding-top:305px;color:#ffffff; font-size: 16px" width="180">	<tr>		<td align="center">'.$cliente->nome.'</td>	</tr></table>