<?php	include_once("../config/config.php");include_once ("../config/cabecalho.php");include_once ("../config/assinatura.php");$conexao = conexao::getInstance();$sql = '	SELECT 		a.matricula,		b.nome	FROM		tab_declaracoes a,		tab_membros b	WHERE		a.matricula = b.matricula	GROUP BY		a.matricula,		b.nome	ORDER BY		b.nome asc';$stm = $conexao->prepare($sql);$stm->execute();$cliente = $stm->fetchAll(PDO::FETCH_OBJ);$dados = "";foreach($cliente as $idc => $value){		$dados .= "		<table width='100%' cellpadding='3' cellspacing='1' border='0' style='font-size: 12px;'>			<tr>				<td width=\"70\" bgcolor='#f0f0f0'><b>Nome Atirador:</b></td>				<td align='left'>{$cliente[$idc]->nome}</td>				<td width=\"60\" bgcolor='#f0f0f0'><b>Matricula:</b></td>				<td width=\"60\" align='left'>{$cliente[$idc]->matricula}</td>			</tr>		</table><br>	";		$sql = "		SELECT 			*		FROM			tab_declaracoes		WHERE			matricula = '{$cliente[$idc]->matricula}'		ORDER BY			matricula,			data_emissao asc	";	$stm = $conexao->prepare($sql);	$stm->execute();	$relatorio = $stm->fetchAll(PDO::FETCH_OBJ);		if($relatorio){		$dados .= "			<table width='100%' cellpadding='3' cellspacing='1' border='0'>				<tr>					<td align='center' bgcolor='#e0e0e0'><b>Declaração Emitida</b></td>					<td align='center' bgcolor='#e0e0e0' width='150'><b>Data Emissão</b></td>				</tr>		";		foreach($relatorio as $idr => $value){			$dados .= "				<tr>					<td>".utf8_decode($relatorio[$idr]->relatorio)."</td>					<td align='center'>".date('d/m/Y H:i:s', strtotime($relatorio[$idr]->data_emissao))."</td>				</tr>			";		}				$dados .= "</table><br><div style='border-top: 1px dashed #000000;'></div><br>";	}			// echo $cliente[$idc]->matricula.' - '.$cliente[$idc]->nome.'<br>';}		//referenciar o DomPDF com namespace	use Dompdf\Dompdf;	// include autoloader	require_once("dompdf/autoload.inc.php");	//Criando a Instancia	$dompdf = new DOMPDF(array('enable_remote' => true));		// Carrega seu HTML	$dompdf->load_html(utf8_encode('<!--*****************************************************************************************************                                       DECLARAÇÃO DE FILIAÇÃO*****************************************************************************************************--><style>table {border-collapse: collapse;} td {border: 1px solid #000000; font-size: 12px;}</style><p align=center style="line-height: 120%; margin-left: 15; margin-right: 15">'.PDF_CABECALHO.'<br><br><br><Br><h4 style="text-align: center;">RELATÓRIO DE EMISSÕES DE DECLARAÇÕES</h4><br><br>'.$dados.'	'));	//Renderizar o html	$dompdf->render();					//Exibibir a pÃ¡gina	$dompdf->stream(		"declaracoes", 		array(			"Attachment" => false //Para realizar o download somente alterar para true		)	);?>